import React, { useState, useEffect } from "react";

export const Projects = ({ projectsRef }) => {
  const [isWeatherHovered, setIsWeatherHovered] = useState(false);
  const [isTikTakToeHovered, setIsTikTakToeHovered] = useState(false);
  const [screenSize, setScreenSize] = useState(getCurrentDimension());

  function getCurrentDimension(){
    return {
        width: window.innerWidth,
        height: window.innerHeight
    }
  }

  useEffect(() => {
    const updateDimension = () => {
        setScreenSize(getCurrentDimension())
    }
    window.addEventListener('resize', updateDimension);


    return(() => {
        window.removeEventListener('resize', updateDimension);
    })
}, [screenSize])

  const handleWeatherMouseEnter = () => {
    if (window.innerWidth <= 1300 && window.innerWidth >= 575) {
      setIsWeatherHovered(true);
    }
  };

  const handleWeatherMouseLeave = () => {
    if (window.innerWidth <= 1300 && window.innerWidth >= 575) {
      setIsWeatherHovered(false);
    }
  };

  const handleTikTakToeMouseEnter = () => {
    if (window.innerWidth <= 1300 && window.innerWidth <= 1300) {
      setIsTikTakToeHovered(true);
    }
  };

  const handleTikTakToeMouseLeave = () => {
    if (window.innerWidth <= 1300 && window.innerWidth <= 1300) {
      setIsTikTakToeHovered(false);
    }
  };


  const weatherDesc = (
    <div className="project-desc">
      <h3>Weather App</h3>
      <p>
          The Weather App is a responsive web application built with React that provides real-time weather information for any location. It allows users to search for a specific location and displays current weather conditions, temperature, humidity, wind speed, and a 5-day forecast.
      </p>
      <p>
          The app fetches weather data from a third-party API and dynamically updates the UI based on the user's search. It features a clean and intuitive user interface, with visually appealing icons and a modern design.
      </p>
      <h3>Technologies</h3>
      <div className="icon-container">
        <img src="./images/react.png"/>
        <img src="./images/api.png"/>
      </div>
    </div>
  )

  const tikTakToeDesc = (            
    <div className="project-desc">
      <h3>Tik Tak Toe</h3>
      <p>
          A simple Tik Tak Toe game features made from HTML, CSS and JavaScript that features a simple and intuitive user interface, with a responsive layout that works well on both desktop and mobile devices. It keeps track of the current player's turn, displays a message when the game ends in a win or a draw, and allows players to reset the game and start a new round.
      </p>
      <h3>Technologies</h3>
      <div className="icon-container">
        <img src="./images/html.png"/>
        <img src="./images/css.png"/>
        <img src="./images/js.png"/>
      </div>
    </div>
  )

  return (
    <>
      <div ref={projectsRef} className="project-container">
        <h1>Projects</h1>
        <div
          className={`project project-1 ${screenSize.width <= 1300 && screenSize.width >= 575 ? 'hovered' : ''}`}
          onMouseEnter={handleWeatherMouseEnter}
          onMouseLeave={handleWeatherMouseLeave}>
            <div className="img-container">
              <img src="./images/wa.png"/>
              {isWeatherHovered && weatherDesc}
            </div>
            {window.innerWidth > 1300 && weatherDesc}
            {screenSize.width <= 575 && weatherDesc}
        </div> 
        <div>

        </div> 
        <div
          className={`project project-2 ${isTikTakToeHovered ? 'hovered' : ''}`}
          onMouseEnter={handleTikTakToeMouseEnter}
          onMouseLeave={handleTikTakToeMouseLeave}>
            {window.innerWidth >= 1300 && tikTakToeDesc}
            <div className="img-container">
              <img src="./images/ttt.png"/>
              {isTikTakToeHovered && tikTakToeDesc}
            </div>
        </div>
        <div>
          {window.innerWidth < 575 && tikTakToeDesc}
          <li>Width: <strong>{screenSize.width}</strong></li>
        		<li>Height: <strong>{screenSize.height}</strong></li>
        </div>
      </div>
    </>
  );
};
