import React, { useRef } from "react";

import { Skills } from "./Skills";
import { Projects } from "./Projects";
import { Contact } from "./Contact"

export const Header = () => {
    const skillsRef = useRef(null);
    const projectsRef = useRef(null);
    const contactRef = useRef(null);

    return (
        <>
        <nav className="navbar navbar-expand-lg">
            <div className="container-fluid">
                <a className="navbar-brand text-white" href="#">Bryan Crow</a>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span class="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
                <ul className="navbar-nav align-items-end">
                    <button className="nav-link text-white top-btn"
                        onClick={() => {
                            skillsRef.current.scrollIntoView({ behavior: 'smooth' });
                        }}>About Me</button>
                    <button className="nav-link text-white top-btn"
                        onClick={() => {
                            projectsRef.current.scrollIntoView({ behavior: 'smooth' });
                        }}>Projects</button>
                    <button className="nav-link text-white top-btn"
                        onClick={() => {
                            skillsRef.current.scrollIntoView({ behavior: 'smooth' });
                        }}>Contact</button>
                </ul>
                </div>
            </div>
        </nav>
         <div className="shape"></div>
         <div className="hero-container">
             <div className="hero-text">
                 <h1>Beautifully Crafted Websites</h1>
                 <div className="home-container">
                     <a className="home-icon nav-link col-sm-1" href="#">GH</a>
                     <a className="home-icon nav-link col-sm-1" href="#">LI</a>
                     <a className="home-icon nav-link col-sm-1" href="#">Resume</a>
                 </div>
             </div>
             <img src="./images/portfolio-logo.svg"/>   
         </div>
            <div ref={skillsRef}>
              <Skills />      
            </div>
            <div>
              <Projects projectsRef={projectsRef}/>      
            </div>
            <div>
                <Contact contactRef={contactRef}/>
            </div>
         </>
    )
}
