import React, { useRef } from "react";

import { Skills } from "./Skills";
import { Projects } from "./Projects";
import { Contact } from "./Contact"

export const Header = () => {
    const skillsRef = useRef(null);
    const projectsRef = useRef(null);
    const contactRef = useRef(null);

    return (
        <>
        <div >
            <div>
            <button className="top-btn"
                onClick={() => {
                    skillsRef.current.scrollIntoView({ behavior: 'smooth' });
            }}>About Me</button>
            <button className=""
                onClick={() => {
                    projectsRef.current.scrollIntoView({ behavior: 'smooth' });
            }}>Projects</button>
            <button className=""
                onClick={() => {
                    contactRef.current.scrollIntoView({ behavior: 'smooth' });
            }}>Contact</button>
            </div>
        </div>
        <div className="shape"></div>
        <div className="hero-container">
            <div className="hero-text">
                <h1>Beautifully Crafted Websites</h1>
                <div className="home-container">
                    <a href="https://github.com/Crowbr1995" target="_blank">
                        <img src="./images/gh.png" alt="GitHub" />
                    </a>
                    <a href="https://www.linkedin.com/in/bryan-crow-b2385b26b/" target="_blank">
                        <img src="./images/li.png" alt="GitHub" />
                    </a>
                    <a href="https://github.com/Crowbr1995" target="_blank">
                        <img src="./images/resume.png" alt="GitHub" />
                    </a>
                 </div>
             </div>
             <img className="logo" src="./images/portfolio-logo.svg"/>   
         </div>
            <div ref={skillsRef}>
              <Skills />      
            </div>
            <div>
              <Projects projectsRef={projectsRef}/>      
            </div>
            <div>
                <Contact contactRef={contactRef}/>
            </div>
         </>
    )
}
